COPY anime_temp(id, title, score, genres, synopsis, type, episodes, status, producers, licensors, studios, source, duration, rating, rank, popularity, favorites, scored_by, members, image_url, genre_award_winning, genre_slice_of_life, genre_fantasy, genre_sci_fi, genre_erotica, genre_romance, genre_horror, genre_boys_love, genre_girls_love, genre_sports, genre_comedy, genre__, genre_gourmet, genre_suspense, genre_supernatural, genre_avant_garde, genre_hentai, genre_drama, genre_mystery, genre_adventure, genre_ecchi, genre_action ) FROM stdin (format csv, ENCODING 'utf-8', DELIMITER ',', HEADER true);
